'use strict';

module.exports = function addSvgReactTransformer(
  batfishConfig,
  template = 'fancy'
) {
  try {
    require('@mapbox/svg-react-transformer-loader');
  } catch (error) {
    if (error.code === 'MODULE_NOT_FOUND') {
      throw new Error(
        'You must install @mapbox/svg-react-transformer-loader before using addSvgReactTransformer'
      );
    } else {
      throw error;
    }
  }

  const webpackLoaders = (batfishConfig.webpackLoaders || []).concat({
    test: /\.svg$/,
    use: {
      loader: '@mapbox/svg-react-transformer-loader',
      options: { template }
    }
  });
  return Object.assign({}, batfishConfig, { webpackLoaders });
};
